import React from 'react'
import './Chat.css'
function Chat() {
    var value = "";
    function onSubmitMessage(e) {
        if(e.keyCode === 13 && e.shiftKey === false) {
            e.preventDefault();
            console.log(e.keyCode);
          }
    }
    return (
        <div className="Chat">
            <div className="messages"> Chat </div>
            <div className="input">
            <textarea placeholder="הקלד/י הודעה" data-placeholder-text="typeHereToChat" maxLength="150" onKeyDown={onSubmitMessage} ></textarea>
            </div>
        </div>
    )
}

export default Chat
