import React, { useState,useContext,useEffect } from 'react';
import { SocketContext,socket } from '../context/socket';
import './Chat.css'
const Chat = (props) => {
    const [value, setValue] = useState("");
    const [messageList,setMessage] = useState([])
    const currentDate = new Date();
    const socket = useContext(SocketContext);
    // For the time now
    Date.prototype.timeNow = function () {
    return ((this.getHours() < 10)?"0":"") + this.getHours() +":"+ ((this.getMinutes() < 10)?"0":"") + this.getMinutes() +":"+ ((this.getSeconds() < 10)?"0":"") + this.getSeconds();
    }

    useEffect(() => {
    // on user connection to chat
        socket.on('user_joined',(data) => {
            socket.emit('message',{
                name : socket.id,
                time : currentDate.timeNow(),
                type: 'connection',
            });
            setMessage([...messageList, data])
            })    
    })
    useEffect(() => {
    // on message received
    socket.on('message_received',data => { 
        console.log(data);
    })

    },0)
    // handles on change input
    const handleChange = (e) => {
        setValue(e.target.value);
    }
    const onSubmitMessage = (e) => {
        if(e.keyCode === 13 && e.shiftKey === false) {
            e.preventDefault();
            socket.emit('message',{
                name : socket.id,
                time : currentDate.timeNow(),
            });
            // resets input
            setValue("")
          }
    }
    return (
        <div className="Chat">
            <div className="messages"> </div>
            <div className="input">
            <textarea placeholder="הקלד/י הודעה" data-placeholder-text="typeHereToChat" maxLength="150" value={value} onChange={handleChange} onKeyDown={onSubmitMessage} ></textarea>
            </div>
        </div>
    )
}

export default Chat
