import React from 'react'
import  { Redirect } from 'react-router-dom'
function LobbyMenu() {
    function onSubmitName (e) {
        if(e.keyCode === 13 && e.shiftKey === false && (/\S/.test(e.target.value))) {
            e.preventDefault();
            localStorage.setItem('settings',{name : e.target.value})
            return <Redirect to='/Room'  />
   
        }

    }
    return (
        <div className="name-input">
            <Redirect to ='/Room' ><input type="text" onKeyDown={onSubmitName}></input></Redirect>      </div>
    )
}

export default LobbyMenu
