import React, { useState,useContext } from 'react';
import { SocketContext } from '../context/socket';
import './Chat.css'
const Chat = (props) => {
    const [value, setValue] = useState("");
    const socket = UseContext(SocketContext);
    // handles on change button
    const handleChange = (e) => {
        setValue(e.target.value);
    }
    const onSubmitMessage = (e) => {
        if(e.keyCode === 13 && e.shiftKey === false) {
            e.preventDefault();
            console.log(value)
            ;
          }
    }
    return (
        <div className="Chat">
            <div className="messages"> </div>
            <div className="input">
            <textarea placeholder="הקלד/י הודעה" data-placeholder-text="typeHereToChat" maxLength="150" value={value} onChange={handleChange} onKeyDown={onSubmitMessage} ></textarea>
            </div>
        </div>
    )
}

export default Chat
