import React, { Component } from 'react'

import './Game.css'
import {SocketContext} from '../../components/context/socket'
import waterballoon from './assets/waterballoon.png'
import User from './Users/User';

export default class Game extends Component {
    static contextType = SocketContext;
    constructor() {
        super();
        this.state = {
            users: []
          };
    }
    componentDidMount(){
        this.context.on("user_connected", data => {
            this.setState({users:data})
            console.log(data);
        })
    }
    getUserNames() {
        let names = [];
        for (var i = 0; i < this.state.users.length; i++) {
                let name = this.state.users[i].name;
                names.push(name);
            }
        return names;
    }
    
    render() {
        return (
            <div className="Balloon-Party">
            <div className="Game">
                <img className="balloon" src={waterballoon}/>
                <div className="users">
                { this.state.users && this.state.users.map((user, i) => {
          return <User message={user} key={i} />;
        })}
                </div>
            </div>
            </div>
        )
    }
}
