import React from 'react'

function Lobby() {
    let username;
    if (localStorage.getItem('settings') !== null) username = localStorage.getItem('settings').name;
    else username = "";
    function onSubmitName (e) {
        if(e.keyCode === 13 && e.shiftKey === false && (/\S/.test(e.target.value))) {
            e.preventDefault();
            localStorage.setItem('settings',{name : e.target.value})
        }

    }
    return (
        <div>
            <textarea placeholder="בחר/י שם" data-placeholder-text="typeHereToChat" maxLength="40" value={username} onKeyDown={onSubmitName} ></textarea>
        </div>
    )
}

export default Lobby
