import React from 'react'

function Lobby() {
    function onSubmitName (e) {
        if(e.keyCode === 13 && e.shiftKey === false && (/\S/.test(this.state.text))) {
            e.preventDefault();
            localStorage.setItem('settings',{name : e.target.value})
        }

    }
    return (
        <div>
            <input type="text" onKeyDown={onSubmitName}></input>
        </div>
    )
}

export default Lobby
