import React, {Component} from 'react';
import noAvatar from '../assets/no-avatar.png'
import heart from '../assets/heart-gui.png'
import './User.css'

class User extends Component {
    render () {
    let deg = 361 * this.props.key;
    const circle_users = 'transform: rotate('+deg+ ')translateX(100px)'
    
    return (
        <div className="user" style={{circle_users}}>
            <img className="avatar" src={noAvatar} alt={this.props.name}/>
            <span className="name" dir="auto">{this.props.name} </span>

        </div>
    )
}
}

export default User
