import React, { useState,useContext,useEffect } from 'react';
import { SocketContext } from '../context/socket';
import Message from './Messages/Message'
import './Chat.css'

class Chat extends React.Component {
    static contextType = SocketContext;
    constructor() {
      super();
      this.state = {
          text: "",
          messageList: [],
        };
    const currentDate = new Date();
    Date.prototype.timeNow = function () {
        return ((this.getHours() < 10)?"0":"") + this.getHours() +":"+ ((this.getMinutes() < 10)?"0":"") + this.getMinutes();
        }        
    }


    componentDidMount() {
    // on user connection to chat
    this.context.on('user_joined',(data) => {
            this.context.emit('message',{
                name : this.context.id,
                time : this.currentDate.timeNow(),
                type: 'connection',
            });
            })    
    this.context.on('user_disconnect',(data) => {
                this.context.emit('message',{
                    name : this.context.id,
                    time : this.currentDate.timeNow(),
                    type: 'disconnection',
                });
                }) 
    // on message received
    this.context.on('message_received',data => { 
        setMessage([...messageList, data])
    })}

    // handles on change input
    handleChange = (e) => {
        this.setState({text: e.target.value})
    }
    onSubmitMessage = (e) => {
        if(e.keyCode === 13 && e.shiftKey === false && (/\S/.test(value))) {
            e.preventDefault();
            const data = {
                name : "איתי זיו",
                time : this.currentDate.timeNow(),
                type: "text",
                text: value
            };
            this.context.emit('message',data);
            setMessage([...messageList, data])
            // resets input
            setValue("")
          }
        }
    render() {
    return (
        <div className="Chat">
        <div className="messages">
        {messageList.map((message, i) => {
          return <Message message={message} key={i} />;
        })}
        </div>
            <div className="input">
            <textarea placeholder="הקלד/י הודעה" data-placeholder-text="typeHereToChat" maxLength="150" value={this.state.text} onChange={handleChange} onKeyDown={onSubmitMessage} ></textarea>
            </div>
        </div>
        );
    }
}
    
export default Chat;
