import React, {Component} from 'react';
import duckuser from '../assets/duck-user.png'
import heart from '../assets/heart-gui.png'
import './User.css'

class User extends Component {
    render () {
    let deg = 45 ;
    const circle_users = 'transform: rotate('+deg+ ')translateX(100px)'
    //transform: `rotate(${deg}deg) rotate(-${deg}deg)`
    return (
        <div className="user" >
            <span className="name" dir="auto">{this.props.name}
            <img className="avatar" src={duckuser} alt={this.props.name}> </img>
            </span>

        </div>
    )
}
}

export default User
