import React, { useState,useContext,useEffect } from 'react';
import { SocketContext,socket } from '../context/socket';
import './Chat.css'
const Chat = (props) => {
    const [value, setValue] = useState("");
    const socket = useContext(socket)
    // on user connection to chat
    socket.on('user_joined',(data) => {
        console.log(data);
        })    
    socket.on('message_received',data => { 
        console.log(data);
    })
    useEffect(() => {
        console.log('reached');
    
    })
    // handles on change button
    const handleChange = (e) => {
        setValue(e.target.value);
    }
    const onSubmitMessage = (e) => {
        if(e.keyCode === 13 && e.shiftKey === false) {
            e.preventDefault();
            socket.emit('message',value);
          }
    }
    return (
        <div className="Chat">
            <div className="messages"> </div>
            <div className="input">
            <textarea placeholder="הקלד/י הודעה" data-placeholder-text="typeHereToChat" maxLength="150" value={value} onChange={handleChange} onKeyDown={onSubmitMessage} ></textarea>
            </div>
        </div>
    )
}

export default Chat
