import React, { Component } from 'react'

import './Game.css'
import {SocketContext} from '../../components/context/socket'
import waterballoon from './assets/waterballoon.png'
import User from './Users/User';

export default class Game extends Component {
    static contextType = SocketContext;
    constructor() {
        super();
        this.state = {
            users: [],
            user_names: []
          };
    }
    componentDidMount(){
        this.context.on("user_joined_received", data => {
            this.setState({users:data.users})
            this.setState({user_names: this.getUserNames(data.users)})
        })
    }
    getUserNames(data) {
        let names = [];
        for (var i = 0; i < data.length; i++) {
                let name = data[i].name;
                console.log(data);
                names.push(name);
            }
        return names;
    }

    render() {
        console.log(this.state.user_names);
        return (
            <div className="Balloon-Party">
            <div className="Game">
                <img className="balloon" src={waterballoon}/>
                <div className="users">
                { this.state.user_names && this.state.user_names.map((user, i) => {
          return <User name={user} key={i} />;
        })}
                </div>
            </div>
            </div>
        )
    }
}
