import React, { Component } from 'react'

import './Game.css'
import {SocketContext} from '../../components/context/socket'
import waterballoon from './assets/waterballoon.png'
import User from './Users/User';

import disintegrate from 'disintegrate'

export default class Game extends Component {
    static contextType = SocketContext;
    constructor() {
        super();
        this.state = {
            users: [],
            user_names: [],
            text: "",
          };
    }
    componentDidMount(){
        disintegrate.init();
        this.context.on("user_joined_received", data => {
            this.setState({users:data.users})
            this.setState({user_names: this.getUserNames(data.users)})
            console.log(data.users)
        })
        this.context.on('user_disconnect',(data) => {this.removeDisconnectedUser(data.socket_id)})
        this.context.on('correctAnswer', () => console.log("correct"))
        this.context.on('incorrectAnswer', () => console.log("incorrect"))

    }
    getUserNames(data) {
        let user_names = [];
        for (var i = 0; i < data.length; i++) {
                let id = data[i].id;
                let name = data[i].name;
                let text = "";
                const user = {id: id,name: name, text: text}
                user_names.push(user);
            }
        return user_names;
    }
     removeDisconnectedUser(id) {
        let users = this.state.users;
        let user_names = this.state.user_names;
        for (var i = 0; i < users.length; i++) {
          if (users[i].socket_id === id) {
              users.splice(i,1)
              user_names.splice(i,1)
              continue
          }
        }
        this.setState({
            users: users,
            user_names: user_names
        })
        console.log(this.state.user_names);
    }
    changeTextValueByID = (id) =>  {
        for (let index = 0; index < this.state.user_names.length; index++) {
            if(this.state.user_names[index]['id'] === "id")
                console.log("found");
        }
    }
    // handles on answer change input
    handleChange = (e) => {
        this.changeTextValueByID(this.context.id);
        this.setState({text: e.target.value})
    }
    // submits answer
    onSubmitMessage = (e) => {
        if(e.keyCode === 13 && e.shiftKey === false && (/\S/.test(this.state.text))) {
            e.preventDefault();
            this.context.emit('checkAnswer',this.state.text);
            console.log('checkAnswer', this.state.text)
          }
        }
        
    render() {
        return (
            <div className="Balloon-Party">
            <div className="Balloon-Background"/>

            <div className="Game">
                <img data-dis-type="self-contained" data-dis-particle-type="ExplodingParticle" data-dis-reduction-factor="100" data-dis-color="[90, 169, 235]" className="balloon"  src={waterballoon} alt="Balloon"/>
                <div className="users">

                { this.state.user_names && this.state.user_names.map(user  => {
                return <User name={user.name}  text={user.text} />;
        })}
                </div>
                <div className="input-answer">
                <textarea placeholder="הקלד/י תשובה" data-placeholder-text="typeheretoanswer" maxLength="40" value={this.state.text} onChange={this.handleChange} onKeyDown={this.onSubmitMessage} ></textarea>
                </div>
            </div>
            </div>
        )
    }
        }
